import React, { useEffect, useState  } from 'react'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button } from 'react-bootstrap';
import Header from '../../components/Header';
import { getAllCategory, addCategory } from '../../actions';
import Input from '../../components/UI/Input';
import CheckboxTree from 'react-checkbox-tree';
import "react-checkbox-tree/lib/react-checkbox-tree.css";
import { AiFillCheckCircle, AiOutlineCheckCircle } from "react-icons/ai";
import { BiDownArrow, BiRightArrow } from "react-icons/bi";

const Category = (props) => {
    const dispatch = useDispatch();
    const category = useSelector(state => state.category);
    const [categoryName, setcategoryName] = useState('');
    const [parentCategoryId, setParentCategoryId] = useState('');
    const [categoryImage, setCategoryImage] = useState('');
    const [show, setShow] = useState(false);
    const [checked, setChecked] = useState([]);
    const [expanded, setExpanded] = useState([]);
    const [checkedArray, setCheckedArray] = useState([]);
    const [expandedArray, setExpandedArray] = useState([]);
    const [updateCategoryModal, setUpdateCategoryModal] = useState(false);

    useEffect(() => {
        console.log('Category.js')
        dispatch(getAllCategory());

    }, []);

    const handleClose = () => {
        const form = new FormData();


        form.append('name', categoryName);
        form.append('parentId', parentCategoryId);
        form.append('CategoryImage', categoryImage);
        dispatch(addCategory(form)).then((result)=>{
            if (result) {
                dispatch(getAllCategory());
            }
        });

        // const cat = {
        //     categoryName,
        //     parentCategoryId,
        //     categoryImage
        // };

       


        setShow(false);
    }
    const handleShow = () => setShow(true);

    const showCategories = (categories) => {

        let categorylist = [];
        for (let category of categories) {
            categorylist.push(
                {
                label: category.name, 
                value: category._id,
                children: category.children.length>0 && showCategories(category.children)
                }  
            );
        }
        return categorylist;
    }

    const createCategoryList = (categories, options = []) => {
        for(let category of categories){
            options.push({value:category._id, name:category.name });
            if(category.children.length > 0){
                createCategoryList(category.children, options)
            }
        }
        
        return options;
    }

    const handleCategoryImage = (e) => {
        setCategoryImage(e.target.files[0]);
    }


    const updateCategory = () => {
        setUpdateCategoryModal(true);
        console.log({checked, expanded});
    }


  


    return (
        <div>
            <h1>categories</h1>
            <button onClick={handleShow}>Add</button>

            <CheckboxTree 
            
            nodes={showCategories(category.categories)}

            checked={checked}
            expanded={expanded}
            onCheck={(checked) => {setChecked(checked)}}
            onExpand={(expanded) => {setExpanded(expanded)}}


                icons={{
                    check: <AiFillCheckCircle/>,  
                    uncheck: <AiOutlineCheckCircle/>,
                    expandClose: <BiRightArrow/>,
                    expandOpen: <BiDownArrow/>,
                    halfCheck: <AiOutlineCheckCircle/>,

                }}
                />

                {/*edit button ka idhar h */}

                <Row>
                <button>Delete</button>
                <button onClick={updateCategory}>Edit</button>
                </Row>
               

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add New Category</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Input
                        value={categoryName}
                        placeholder={'Category Name'}
                        onChange={(e) => setcategoryName(e.target.value)}
                    />

                    <select 
                    className = "form-control"
                    value = {parentCategoryId}
                    onChange = {(e) => setParentCategoryId(e.target.value)}>

                        <option>Select category</option>
                        {
                            createCategoryList(category.categories).map(option =>
                            <option key={option.value} value={option.value}>{option.name}</option>)    
                        }
                    </select>

                    <input type="file" name= "categoryImage" onChange={handleCategoryImage} />



                </Modal.Body>
                <Modal.Footer>

                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* Edit Category */}

            <Modal show={updateCategoryModal} 
            handleClose={ () => setUpdateCategoryModal(true)}
            size='lg'
            >
                <Modal.Header closeButton>
                    <Modal.Title>Update Category</Modal.Title>
                </Modal.Header>
                <Modal.Body>

                    <Row> 
                        <Col>
                        <h6>Expanded</h6>
                        </Col>
                    </Row>

                    
                    <Row>
                        <Col>
                        <Input
                        value={categoryName}
                        placeholder={'Category Name'}
                        onChange={(e) => setcategoryName(e.target.value)}
                        />
                        </Col>
                    </Row>

                    <select 
                    className = "form-control"
                    value = {parentCategoryId}
                    onChange = {(e) => setParentCategoryId(e.target.value)}>

                        <option>Select category</option>
                        {
                            createCategoryList(category.categories).map(option =>
                            <option key={option.value} value={option.value}>{option.name}</option>)    
                        }
                    </select>

                    <Row>
                        <Col>

                    <select 
                    className = "form-control"
                    >
                        <option value ="">Select Type</option>
                        <option value ="store">Select Type</option>
                        <option value ="product">Select Type</option>
                        <option value ="page">Select Type</option>
                    </select>

                    </Col>
                    </Row>
                


                    <input type="file" name= "categoryImage" onChange={handleCategoryImage} />



                </Modal.Body>
                <Modal.Footer>

                    <Button variant="primary" onClick={handleClose}>
                        Update Changes
                    </Button>
                </Modal.Footer>
            </Modal>

        </div>
    );
}

export default Category;
